$(document).ready(function () {
  $("#search-btn").click(function () {
    !(function (t) {
      (API_URL = `https://api.weatherapi.com/v1/current.json?key=c5eb9bcba2324e27b0c45812230202&q=${t}&aqi=yes`),
        $.getJSON(API_URL, function (t) {
          console.log(t);
          let n = `${t.location.name}, ${t.location.region}, ${t.location.country}`,
            e = t.current.condition.text,
            i = t.current.condition.icon,
            o = t.current.temp_c,
            a = t.current.precip_mm,
            c = t.current.wind_kph,
            r = t.current.humidity;
          $("#location")
            .css({ transition: "all 0.5s ease-in-out" })
            .text(`Weather in ${n}`),
            $("#weather-condition")
              .css({ transition: "all 0.5s ease-in-out" })
              .text(e),
            $("#icon").attr("src", i),
            $("#temperature")
              .css({ transition: "all 0.5s ease-in-out" })
              .text(`${o}â„ƒ`),
            $("#humidity")
              .css({ transition: "all 0.5s ease-in-out" })
              .text(`${r}%`),
            $("#precipitation")
              .css({ transition: "all 0.5s ease-in-out" })
              .text(`${a} mm`),
            $("#wind-speed")
              .css({ transition: "all 0.5s ease-in-out" })
              .text(`${c} km/h`);
        });
    })($("#city").val());
  });
});
